import 'package:covid_test_app/components/rounded_button.dart';import 'package:flutter/material.dart';import 'login.dart';import 'registration.dart';class WelcomeScreen extends StatefulWidget {  static const String id = 'welcome_screen';  @override  _WelcomeScreenState createState() => _WelcomeScreenState();}class _WelcomeScreenState extends State<WelcomeScreen> {  @override  Widget build(BuildContext context) {    return Container(      decoration: BoxDecoration(        image: DecorationImage(          image: AssetImage('images/covidmap.png'),          fit: BoxFit.cover,        ),      ),      padding: EdgeInsets.symmetric(horizontal: 24.0),      child: Column(        mainAxisAlignment: MainAxisAlignment.center,        crossAxisAlignment: CrossAxisAlignment.stretch,        children: <Widget>[          SizedBox(            height: 48.0,          ),          RoundedButton(            title: 'Sign In',            colour: Colors.redAccent[400],            onPressed: () {              Navigator.pushNamed(context, LoginScreen.id);            },          ),          RoundedButton(            title: 'Create New Account',            colour: Colors.redAccent[400],            onPressed: () {              Navigator.pushNamed(context, RegistrationScreen.id);            },          ),        ],      ),    );  }}